#!/bin/sh

set -e

ls
cd frontend

cat > .env << EOF
SEOCONFIG=$(SEOCONFIG)
AUTHCONFIG=$(AUTHCONFIG)
APIURL=$(APIURL)
USESECURE=$(USESECURE)
RECAPTCHASITEKEY=$(RECAPTCHASITEKEY)
STRIPECONFIG=$(STRIPECONFIG)
MAPSAUTOAPIKEY=$(MAPSAUTOAPIKEY)
SHORTLINKURL=$(SHORTLINKURL)
EOF

yarn
yarn predeploy
cd -

node /action.js
